protected void GridView1_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        int termId = Convert.ToInt32(e.Row.Cells[0].Text);\n        switch (termId)\n        {\n            case 1:\n                e.Row.Cells[0].Text = "30 days";\n                break;\n            case 2:\n                e.Row.Cells[0].Text = "60 days";\n                break;\n        }\n    }\n}