using System;\nusing System.Runtime.InteropServices;\n\nclass MapXTest\n{\n    [DllImport("ole32.dll")]\n    static extern int CoInitialize(IntPtr pvReserved);\n\n    [DllImport("ole32.dll")]\n    static extern int CoCreateInstance(ref Guid clsid, IntPtr punkOuter, \n        int context, ref Guid iid, out IntPtr punk);\n\n    static void Main(string[] args)\n    {\n        Guid clsid = new Guid("your-MapX-guid-here");\n        Guid iid = new Guid("your-MapX-iid-here");\n\n        IntPtr punk;\n        int hr = CoCreateInstance(ref clsid, IntPtr.Zero, 1, ref iid, out punk);\n\n        if (hr != 0)\n        {\n            Console.WriteLine("Failed to create MapX instance: " + hr);\n        }\n        else\n        {\n            Console.WriteLine("MapX instance created successfully");\n        }\n    }\n}