using System.IO;\nusing System.Security.AccessControl;\n\nstring webConfigPath = @"C:\Inetpub\wwwroot\wss\VirtualDirectories\8443\web.config";\nFileInfo fileInfo = new FileInfo(webConfigPath);\n\nFileSecurity fileSecurity = fileInfo.GetAccessControl();\nAuthorizationRuleCollection rules = fileSecurity.GetAccessRules(true, true, typeof(NTAccount));\n\nforeach (FileSystemAccessRule rule in rules)\n{\n    if (rule.IdentityReference.Value == "mosssvc")\n    {\n        Console.WriteLine($"mosssvc has {rule.FileSystemRights} rights on the web.config file.");\n    }\n}