// Example of using MEF to manage internal components\n[ImportMany]\nprivate IEnumerable<IEditorComponent> editorComponents;\n\n// Example of using MAF to load external add-ins\nAddInStore store = new AddInStore();\nstore.Update();\nAddInToken token = store.FindAddIn(typeof(IExternalAddIn), null);\nIExternalAddIn addIn = token.Activate<IExternalAddIn>(AddInSecurityLevel.Internet);