using Microsoft.Graph;\n\n// Replace with your client's tenant ID\nstring tenantId = "client_tenant_id";\n\n// Replace with your client's client ID\nstring clientId = "client_client_id";\n\n// Replace with your client's client secret\nstring clientSecret = "client_client_secret";\n\nGraphServiceClient graphClient = new GraphServiceClient(\n    new DelegateAuthenticationProvider(\n        (requestMessage) =>\n        {\n            // Authenticate using client credentials\n            var token = GetAccessTokenAsync(tenantId, clientId, clientSecret).Result;\n            requestMessage.Headers.Authorization = new AuthenticationHeaderValue("Bearer", token);\n            return Task.CompletedTask;\n        }));\n\n// Access the client's mailbox data\nvar inboxMessages = graphClient.Me.MailFolders.Inbox.Messages.Request().GetAsync().Result;