using System;\nusing System.Runtime.InteropServices;\nusing System.Windows.Forms;\n\nnamespace YourApp\n{\n    [ComVisible(true)]\n    public class RedemptionInstaller : IDisposable\n    {\n        [DllImport("kernel32")]\n        private static extern IntPtr LoadLibrary(string lpFileName);\n\n        public RedemptionInstaller()\n        {\n            string redemptionPath = Path.Combine(Application.StartupPath, "Redemption.dll");\n            IntPtr hModule = LoadLibrary(redemptionPath);\n            if (hModule == IntPtr.Zero)\n            {\n                throw new Exception("Failed to load Redemption DLL");\n            }\n        }\n\n        public void Dispose()\n        {\n            // Nothing to dispose\n        }\n    }\n}