using System.Diagnostics;\nusing System.Security.Principal;\n\n// ...\n\nvar eventLog = new EventLog("MyEventLog");\nvar entry = new EventLogEntry();\n\nentry.Message = "My Special Message";\nentry.UserName = WindowsIdentity.GetCurrent().Name;\n\nvar eventData = new EventLogEntryDataCollection();\neventData.Add(new EventLogEntryData(entry.Message));\neventData.Add(new EventLogEntryData(entry.UserName));\n\neventLog.WriteEvent(new EventInstance(0, 0, EventLogEntryType.Information), eventData);