using Microsoft.TeamFoundation.Build.Client;\nusing Microsoft.TeamFoundation.Client;\n\n// Connect to TFS\nTfsTeamProjectCollection teamProjectCollection = new TfsTeamProjectCollection("http://your-tfs-server:8080/tfs");\nIBuildServer buildServer = teamProjectCollection.GetService<IBuildServer>();\n\n// Create a new build definition\nIBuildDefinition buildDefinition = buildServer.CreateBuildDefinition("Your Team Project", "Your Build Definition Name");\n\n// Set up the build definition\nbuildDefinition.DefaultDropLocation = @"\\your-drop-location";\nbuildDefinition.Process = buildServer.GetBuildProcess("Your Build Process Template");\n\n// Create a new build request\nIBuildRequest buildRequest = buildServer.CreateBuildRequest(buildDefinition);\n\n// Queue the build\nbuildServer.QueueBuild(buildRequest);