string cookieContent = "your cookie content";\nAes aes = Aes.Create();\naes.Key = your_key_bytes;\naes.IV = your_iv_bytes;\n\nICryptoTransform encryptor = aes.CreateEncryptor(aes.Key, aes.IV);\nbyte[] encryptedBytes = encryptor.TransformFinalBlock(Encoding.UTF8.GetBytes(cookieContent), 0, cookieContent.Length);\n\n// Set the encrypted cookie\nHttpCookie cookie = new HttpCookie("YourCookieName", Convert.ToBase64String(encryptedBytes));\nResponse.Cookies.Add(cookie);