using Microsoft.Office.Interop.Word;\n\n// Create a new instance of Word\nType wordType = Type.GetTypeFromProgID("Word.Application");\nobject wordApp = Activator.CreateInstance(wordType);\n\n// Open a document\nobject wordDoc = wordApp.GetType().InvokeMember("Documents", BindingFlags.GetProperty, null, wordApp, null);\nwordDoc.GetType().InvokeMember("Open", BindingFlags.InvokeMethod, null, wordDoc, new object[] { "example.docx" });\n\n// Get the text of the first paragraph\nobject paragraph = wordDoc.GetType().InvokeMember("Paragraphs", BindingFlags.GetProperty, null, wordDoc, null);\nstring text = paragraph.GetType().InvokeMember("Range", BindingFlags.GetProperty, null, paragraph, null).GetType().InvokeMember("Text", BindingFlags.GetProperty, null, null, null);