public class ActionInvokable<T> : Invokable<T>\n{\n    private readonly Action<T> action;\n\n    public ActionInvokable(Action<T> action)\n    {\n        this.action = action;\n    }\n\n    public override void Invoke(T arg)\n    {\n        action(arg);\n    }\n}\n\npublic class FuncInvokable<T, TResult> : Invokable<T>\n{\n    private readonly Func<T, TResult> func;\n\n    public FuncInvokable(Func<T, TResult> func)\n    {\n        this.func = func;\n    }\n\n    public override void Invoke(T arg)\n    {\n        func(arg);\n    }\n}