using iTextSharp.text;\nusing iTextSharp.text.pdf;\nusing System.IO;\n\npublic void GridViewToPdf(GridView gridView, string fileName)\n{\n    using (MemoryStream ms = new MemoryStream())\n    {\n        Document document = new Document(PageSize.A4, 10, 10, 10, 10);\n        PdfWriter.GetInstance(document, ms);\n        document.Open();\n\n        // Add a header\n        Paragraph header = new Paragraph(gridView.Caption);\n        header.Font = FontFactory.GetFont("Arial", 14, Font.BOLD);\n        document.Add(header);\n\n        // Create a table\n        PdfPTable table = new PdfPTable(gridView.Columns.Count);\n        table.WidthPercentage = 100;\n\n        // Add table headers\n        foreach (DataControlField column in gridView.Columns)\n        {\n            PdfPCell cell = new PdfPCell(new Phrase(column.HeaderText));\n            cell.BackgroundColor = BaseColor.LIGHT_GRAY;\n            table.AddCell(cell);\n        }\n\n        // Add table rows\n        foreach (GridViewRow row in gridView.Rows)\n        {\n            foreach (TableCell cell in row.Cells)\n            {\n                PdfPCell pdfCell = new PdfPCell(new Phrase(cell.Text));\n                table.AddCell(pdfCell);\n            }\n        }\n\n        document.Add(table);\n        document.Close();\n\n        // Save the PDF to a file\n        byte[] pdfBytes = ms.ToArray();\n        File.WriteAllBytes(fileName, pdfBytes);\n    }\n}