[DllImport("kernel32.dll")]\nstatic extern IntPtr LoadLibrary(string lpFileName);\n\n[DllImport("kernel32.dll")]\nstatic extern IntPtr GetProcAddress(IntPtr hModule, string lpProcName);\n\n// Load the OCX\nIntPtr hModule = LoadLibrary("myocx.ocx");\n\n// Get the function pointer for the control's CreateInstance function\nIntPtr pCreateInstance = GetProcAddress(hModule, "CreateInstance");\n\n// Create an instance of the control\nIntPtr instance = Marshal.CreateInstance(typeof(MyOcxControl), pCreateInstance);