using Dapper;\n\n// Create a DataTable with your TVP data\nDataTable tvpData = new DataTable();\ntvpData.Columns.Add("Id", typeof(int));\ntvpData.Columns.Add("Name", typeof(string));\n// ... add data to the table ...\n\n// Create a Dapper command with the TVP\nvar cmd = new CommandDefinition("EXEC MyStoredProcedure @tvp", new { tvp = tvpData.AsTableValuedParameter("MyTableType") });\n\n// Execute the command\nvar results = connection.Query<MyResultType>(cmd);