public void SaveResume(HttpPostedFileBase file)\n{\n    Resume resume = new Resume();\n    resume.PdfFile = new byte[file.ContentLength];\n    file.InputStream.Read(resume.PdfFile, 0, file.ContentLength);\n\n    // Save the resume entity using NHibernate\n    using (ISession session = NHibernateHelper.OpenSession())\n    {\n        session.Save(resume);\n        session.Flush();\n    }\n}