private string GrabImage(string htmlContent)\n{\n    HtmlAgilityPack.HtmlDocument htmlDoc = new HtmlAgilityPack.HtmlDocument();\n    htmlDoc.LoadHtml(htmlContent);\n\n    if (htmlDoc.ParseErrors.Count > 0)\n    {\n        // Handle parsing errors\n        return null;\n    }\n\n    HtmlAgilityPack.HtmlNode imageNode = htmlDoc.DocumentNode.SelectSingleNode("//img");\n    if (imageNode != null)\n    {\n        return imageNode.OuterHtml; // Use OuterHtml to get the entire img tag\n    }\n    else\n    {\n        return null;\n    }\n}