[OperationContract]\n[WebInvoke(Method = "POST", UriTemplate = "/MyMethod")]\npublic void MyMethod(MyRequest request)\n{\n    try\n    {\n        // Process the request\n        MySuccessResponse response = new MySuccessResponse { Message = "Success" };\n        WebOperationContext.Current.OutgoingResponse.StatusCode = HttpStatusCode.OK;\n        WebOperationContext.Current.OutgoingResponse.ContentType = "application/json";\n        return new MemoryStream(Encoding.UTF8.GetBytes(JsonConvert.SerializeObject(response)));\n    }\n    catch (Exception ex)\n    {\n        MyErrorResponse errorResponse = new MyErrorResponse { ErrorMessage = ex.Message };\n        WebOperationContext.Current.OutgoingResponse.StatusCode = HttpStatusCode.InternalServerError;\n        WebOperationContext.Current.OutgoingResponse.ContentType = "application/json";\n        return new MemoryStream(Encoding.UTF8.GetBytes(JsonConvert.SerializeObject(errorResponse)));\n    }\n}\n\n[DataContract]\npublic class MySuccessResponse\n{\n    [DataMember]\n    public string Message { get; set; }\n}\n\n[DataContract]\npublic class MyErrorResponse\n{\n    [DataMember]\n    public string ErrorMessage { get; set; }\n}