public void hladame_fieldy()\n{\n    //fieldy\n    string nazov_fieldu;\n    string field = "//input[@type='text']";\n\n    // Get all input fields with type 'text'\n    var fields = selenium.FindElements(By.XPath(field));\n\n    // Iterate over the fields\n    foreach (var fieldElement in fields)\n    {\n        nazov_fieldu = fieldElement.GetAttribute("name");\n        Console.WriteLine(nazov_fieldu);\n    }\n\n    Console.WriteLine("Celkovy pocet fieldov je = " + fields.Count);\n}