using System;\nusing System.Collections.Generic;\nusing UPnP;\n\nnamespace MyMediaServer\n{\n    public class MediaServer : UPnPDevice\n    {\n        private readonly List<UPnPResource> _resources = new List<UPnPResource>();\n\n        public MediaServer()\n        {\n            FriendlyName = "My Media Server";\n            Manufacturer = "My Company";\n            ModelName = "My Media Server Model";\n\n            // Add some sample resources (e.g. photos)\n            _resources.Add(new UPnPResource("http://example.com/photo1.jpg", "image/jpeg"));\n            _resources.Add(new UPnPResource("http://example.com/photo2.jpg", "image/jpeg"));\n        }\n\n        public override IEnumerable<UPnPResource> GetResources()\n        {\n            return _resources;\n        }\n    }\n\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            var server = new MediaServer();\n            server.Start();\n\n            Console.WriteLine("Media server started. Press any key to stop...");\n            Console.ReadKey();\n            server.Stop();\n        }\n    }\n}