using Microsoft.Win32;\n\n// Open the 32-bit view of the registry\nusing (RegistryKey key = RegistryKey.OpenBaseKey(RegistryHive.LocalMachine, RegistryView.Registry32))\n{\n    // Open the subkey\n    using (RegistryKey subkey = key.OpenSubKey("YourSubkeyPath"))\n    {\n        // Read the value\n        object value = subkey.GetValue("YourValueName");\n        // Do something with the value\n    }\n}