using System.Data.SqlClient;\n\n// Create a connection to the database\nstring connectionString = "Your connection string";\nSqlConnection connection = new SqlConnection(connectionString);\n\n// Get the schema of the stored procedure\nDataTable schema = connection.GetSchema("ProcedureParameters", new[] { "YourStoredProcedureName" });\n\n// Parse the schema to determine the parameters and their types\nforeach (DataRow row in schema.Rows)\n{\n    string parameterName = row["ParameterName"].ToString();\n    SqlDbType parameterType = (SqlDbType)row["DataType"];\n    Console.WriteLine($"Parameter: {parameterName}, Type: {parameterType}");\n}