using System.Runtime.Remoting.Proxies;\n\npublic class MyPocoProxy : RealProxy\n{\n    private MyPoco _target;\n\n    public MyPocoProxy(MyPoco target) : base(typeof(MyPoco))\n    {\n        _target = target;\n    }\n\n    public override IMessage Invoke(IMessage msg)\n    {\n        // Do something before the invocation\n        var result = _target.GetType().GetMethod(msg.MethodName).Invoke(_target, msg.Args);\n        // Do something after the invocation\n        return new ReturnMessage(result, null, 0, null, msg);\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        var poco = new MyPoco();\n        var proxy = new MyPocoProxy(poco).GetTransparentProxy();\n\n        // Use the proxy as you would the original poco\n        proxy.GetType().GetProperty("Name").SetValue(proxy, "John", null);\n    }\n}