using System;\nusing System.IO;\nusing DocumentFormat.OpenXml.Packaging;\nusing DocumentFormat.OpenXml.Wordprocessing;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\npublic class WordToPdfConverter\n{\n    public void Convert(string wordFilePath, string pdfFilePath)\n    {\n        using (WordprocessingDocument wordDoc = WordprocessingDocument.Open(wordFilePath, true))\n        {\n            using (FileStream pdfStream = new FileStream(pdfFilePath, FileMode.Create))\n            {\n                Document doc = new Document();\n                PdfWriter writer = PdfWriter.GetInstance(doc, pdfStream);\n                doc.Open();\n\n                foreach (Text text in wordDoc.MainDocumentPart.Document.Body.Elements<Text>())\n                {\n                    doc.Add(new Paragraph(text.InnerText));\n                }\n\n                doc.Close();\n            }\n        }\n    }\n}