XPathDocument doc = new XPathDocument(HttpWebResponse.GetResponseStream());\nXPathNavigator nav = doc.CreateNavigator();\n\nXmlNamespaceManager nsmgr = new XmlNamespaceManager(nav.NameTable);\nnsmgr.AddNamespace("geo", "http://where.yahooapis.com/v1/schema"); // adjust the namespace URI if needed\n\nXPathNodeIterator nodes = nav.Select("/geo:places", nsmgr);\n\nwhile (nodes.MoveNext())\n{\n    Console.WriteLine(nodes.Current.Name); // should print "places"\n}