public class ResultSet\n{\n    public int ID { get; set; }\n    public string Name { get; set; }\n    public string Value { get; set; }\n}\n\npublic List<ResultSet> GetObject(string objectType, bool itemState, bool isPublished)\n{\n    using (MyDataContext dc = new MyDataContext())\n    {\n        var results = dc.ExecuteQuery<ResultSet>("EXEC dbo.p_GetObject @ObjectType, @ItemState, @IsPublished",\n            new SqlParameter("@ObjectType", objectType),\n            new SqlParameter("@ItemState", itemState),\n            new SqlParameter("@IsPublished", isPublished));\n        return results.ToList();\n    }\n}