using iTextSharp.text.pdf;\n\npublic void SplitPdfIntoPages(string filePath)\n{\n    PdfReader pdfReader = new PdfReader(filePath);\n    for (int page = 1; page <= pdfReader.NumberOfPages; page++)\n    {\n        PdfWriter pdfWriter = PdfWriter.GetInstance(pdfReader, new FileStream($"page{page}.pdf", FileMode.Create));\n        pdfWriter.Open();\n        pdfReader.SetPageContent(page);\n        pdfWriter.Close();\n    }\n    pdfReader.Close();\n}