using System;\nusing System.Data.Linq;\n\npublic class PostResult\n{\n    public int PostId { get; set; }\n    public DateTime PostedDateTimeUtc { get; set; }\n}\n\npublic class MyDataContext : DataContext\n{\n    // ...\n}\n\npublic PostResult GetLatestPost(MyDataContext context)\n{\n    string sql = "select distinct top 1 'PostId' = isnull(RootPost,Id), PostedDateTimeUtc from Post order by PostedDateTimeUtc desc";\n    var result = context.ExecuteQuery<PostResult>(sql);\n    return result.FirstOrDefault();\n}