using System.Web.Http;\nusing System.Web.Http.Results;\n\npublic class GridController : ApiController\n{\n    [HttpGet]\n    public JsonResult GetData(int page, int rows, string sidx, string sord)\n    {\n        // Sample data\n        var data = new[]\n        {\n            new { id = 1, name = "John", age = 30 },\n            new { id = 2, name = "Jane", age = 25 },\n            new { id = 3, name = "Bob", age = 40 },\n        };\n\n        // Paging and sorting logic\n        var pagedData = data.Skip((page - 1) * rows).Take(rows);\n        if (sord == "desc")\n        {\n            pagedData = pagedData.OrderByDescending(x => x.id);\n        }\n        else\n        {\n            pagedData = pagedData.OrderBy(x => x.id);\n        }\n\n        // Return JSON data\n        return Json(new\n        {\n            page,\n            total = (int)Math.Ceiling((double)data.Length / rows),\n            records = data.Length,\n            rows = pagedData\n        });\n    }\n}