public static bool ValidateFIN(string fin)\n{\n    if (fin.Length != 9)\n        return false;\n\n    int letter1 = fin[0] - 'A' + 1; // Convert to numerical value\n    int letter2 = fin[1] - 'A' + 1;\n\n    int sum = 0;\n    for (int i = 2; i < 9; i++)\n    {\n        int digit = int.Parse(fin[i].ToString());\n        int weight = i - 1; // Weight starts from 2\n        sum += digit * weight;\n    }\n\n    sum += letter1 + letter2;\n\n    return (sum % 11) == 0;\n}