using System;\nusing System.Net.NetworkInformation;\n\nclass Program\n{\n    static void Main()\n    {\n        // Get a list of network interfaces\n        NetworkInterface[] interfaces = NetworkInterface.GetAllNetworkInterfaces();\n\n        // Iterate over each interface\n        foreach (NetworkInterface nic in interfaces)\n        {\n            // Get the IP properties for the interface\n            IPInterfaceProperties ipProps = nic.GetIPProperties();\n\n            // Get the ARP cache\n            IPv4InterfaceProperties arpCache = ipProps.GetIPv4Properties();\n\n            // Iterate over each entry in the ARP cache\n            foreach (ArpEntry entry in arpCache.ArpEntries)\n            {\n                Console.WriteLine($"IP Address: {entry.Address}");\n                Console.WriteLine($"MAC Address: {entry.PhysicalAddress}");\n                Console.WriteLine($"Interface: {entry.Interface}");\n                Console.WriteLine();\n            }\n        }\n    }\n}