// After successful OpenID authentication\nif (openid.IsAuthenticated)\n{\n    // Check if the OpenID is associated with an existing account\n    if (!IsExistingAccount(openid.Identity))\n    {\n        // Redirect to registration page\n        Response.Redirect("~/Register.aspx?openid=" + openid.Identity);\n    }\n    else\n    {\n        // Sign in the user using FormsAuthentication\n        FormsAuthentication.RedirectFromLoginPage(openid.Identity, true);\n    }\n}