mshtml.HTMLDocumentClass doc = ...; // your document object\n\n// Get all input elements (buttons, textboxes, etc.)\nmshtml.IHTMLElementCollection inputElements = doc.getElementsByTagName("input");\n\n// Iterate through the input elements\nforeach (mshtml.IHTMLInputElement inputElement in inputElements)\n{\n    // Check if the input element is a button\n    if (inputElement.type == "submit")\n    {\n        // Check if the button has the value "Web Search"\n        if (inputElement.value == "Web Search")\n        {\n            // Click the button\n            inputElement.click();\n            break;\n        }\n    }\n}