using System.Data.Odbc;\n\n// Create an OdbcCommand object\nOdbcCommand cmd = new OdbcCommand("YourStoredProcedureName", YourOdbcConnection);\n\n// Create an OdbcParameter object for the XML parameter\nOdbcParameter param = new OdbcParameter("@XmlData", OdbcType.NVarChar);\nparam.Size = 4000; // or a larger value if needed\nparam.Value = YourXmlString;\n\n// Add the parameter to the command\ncmd.Parameters.Add(param);