using Microsoft.Office.Interop.Outlook;\n\npublic class OutlookHelper\n{\n    public string[] FindPstFiles()\n    {\n        Application app = new Application();\n        Namespace ns = app.GetNamespace("MAPI");\n\n        // Logon to the Outlook profile\n        ns.Logon("", "", false, false);\n\n        // Get the PST file location\n        MAPIFolder root_folder = ns.GetDefaultFolder(OlDefaultFolders.olFolderInbox).Parent;\n        string[] pst_files = new string[root_folder.Folders.Count];\n        for (int i = 1; i <= root_folder.Folders.Count; i++)\n        {\n            MAPIFolder folder = root_folder.Folders[i];\n            if (folder.DefaultItemType == OlItemType.olMailItem)\n            {\n                pst_files[i - 1] = folder.FilePath;\n            }\n        }\n\n        // Logoff and release resources\n        ns.Logoff();\n        ns = null;\n        app = null;\n\n        return pst_files;\n    }\n}