using System;\nusing System.IO;\nusing System.Web;\n\npublic class TraceLogger : IHttpHandler\n{\n    public void ProcessRequest(HttpContext context)\n    {\n        var traceOutput = context.Request.QueryString["trace"];\n        using (var writer = new StreamWriter("trace.log", true))\n        {\n            writer.Write(traceOutput);\n        }\n    }\n\n    public bool IsReusable\n    {\n        get { return true; }\n    }\n}