using System;\nusing System.Security.Cryptography;\nusing System.Security.Cryptography.X509Certificates;\n\nclass CreateSelfSignedCertificate\n{\n    static void Main(string[] args)\n    {\n        // Create a new RSA key pair\n        using (var rsa = RSA.Create(2048))\n        {\n            // Create a self-signed certificate\n            var cert = new X509Certificate2(rsa.ExportParameters(true));\n            cert.Subject = "CN=Root CA";\n            cert.Issuer = cert.Subject;\n            cert.NotBefore = DateTime.UtcNow;\n            cert.NotAfter = DateTime.UtcNow.AddYears(10);\n\n            // Save the certificate to a file\n            File.WriteAllBytes("rootCA.crt", cert.Export(X509ContentType.Cert));\n            File.WriteAllBytes("rootCA.key", rsa.ExportPkcs8PrivateKey());\n        }\n    }\n}