// WCF service code\npublic class MyService : IMyService\n{\n  public void DoSomething()\n  {\n    // Validate the client's identity\n    var userAgent = OperationContext.Current.IncomingMessageProperties["httpRequest"].Headers["User-Agent"];\n    if (userAgent != "My Silverlight Application")\n    {\n      throw new SecurityException("Invalid client");\n    }\n    // Process the request\n  }\n}