using System.Data.SqlClient;\n\n// create a connection string with the desired credentials\nstring connectionString = "Data Source=myServerAddress;Initial Catalog=myDataBase;Integrated Security=SSPI;";\n\n// create a SqlConnection object with the desired credentials\nSqlConnection connection = new SqlConnection(connectionString);\nconnection.Credential = new SqlCredential("myDomain\myUsername", "myPassword");\n\n// open the connection\nconnection.Open();