using System;\nusing System.Net.Http;\nusing System.Xml;\n\nnamespace OracleHCMWebServiceExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Set up the web service URL and credentials\n            string url = "https://your-oracle-hcm-cloud-instance.com/hcm/api/v1/employees";\n            string username = "your-username";\n            string password = "your-password";\n\n            // Create an HTTP client and set the authentication headers\n            HttpClient client = new HttpClient();\n            client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Basic", Convert.ToBase64String(System.Text.ASCIIEncoding.ASCII.GetBytes(string.Format("{0}:{1}", username, password))));\n\n            // Send a GET request to retrieve employee data\n            HttpResponseMessage response = client.GetAsync(url).Result;\n\n            // Parse the XML response\n            XmlDocument xmlDoc = new XmlDocument();\n            xmlDoc.LoadXml(response.Content.ReadAsStringAsync().Result);\n\n            // Process the employee data\n            XmlNodeList employees = xmlDoc.SelectNodes("/employees/employee");\n            foreach (XmlNode employee in employees)\n            {\n                Console.WriteLine(employee.SelectSingleNode("name").InnerText);\n            }\n        }\n    }\n}