using System;\nusing System.IO;\nusing System.IO.Packaging;\nusing System.Xml;\n\npublic static string TextDump(string filePath)\n{\n    using (Package package = Package.Open(filePath, FileMode.Open, FileAccess.Read))\n    {\n        Uri documentUri = new Uri("/word/document.xml", UriKind.Relative);\n        PackagePart documentPart = package.GetPart(documentUri);\n        using (Stream stream = documentPart.GetStream())\n        {\n            XmlDocument xmlDoc = new XmlDocument();\n            xmlDoc.Load(stream);\n\n            StringBuilder builder = new StringBuilder();\n\n            XmlNodeList textNodes = xmlDoc.SelectNodes("//w:t", GetNamespaceManager(xmlDoc));\n            foreach (XmlNode node in textNodes)\n            {\n                builder.AppendLine(node.InnerText);\n            }\n\n            return builder.ToString();\n        }\n    }\n}\n\nprivate static XmlNamespaceManager GetNamespaceManager(XmlDocument xmlDoc)\n{\n    XmlNamespaceManager nsmgr = new XmlNamespaceManager(xmlDoc.NameTable);\n    nsmgr.AddNamespace("w", "http://schemas.openxmlformats.org/wordprocessingml/2006/main");\n    return nsmgr;\n}