using System.Windows;\nusing System.Windows.Documents;\nusing System.Windows.Media;\n\npublic class DropdownInline : Inline\n{\n    public override void BuildVisualTree(DrawingContext drawingContext)\n    {\n        // Create a dropdown\n        var dropdown = new ComboBox();\n        dropdown.ItemsSource = new[] { "Option 1", "Option 2", "Option 3" };\n\n        // Add the dropdown to the visual tree\n        drawingContext.DrawRectangle(Brushes.Transparent, null, new Rect(0, 0, 100, 20));\n        drawingContext.DrawVisual(dropdown);\n    }\n}