using System.Data.Entity;\nusing System.Data.Entity.Infrastructure;\n\n// Assume 'dataset' is your disconnected dataset\nDataSet dataset = ...;\n\n// Create a DbContext instance\nvar context = new DbContext("name=MyContext");\n\n// Create a DbModelBuilder instance\nvar modelBuilder = new DbModelBuilder();\n\n// Create the model from the dataset\nmodelBuilder.Entity<DataRow>().HasEntitySetName("MyEntity");\n\n// Configure the model\nmodelBuilder.Entity<DataRow>()\n    .HasKey(row => row["Id"])\n    .ToTable("MyTable");\n\n// Create the model\nvar model = modelBuilder.Build(context);\n\n// Use the model\nvar myEntities = context.Set<DataRow>().ToList();