using System.Data.SqlClient;\nusing System.Reflection;\n\n// Get the SqlConnection instance\nSqlConnection connection = /* get the instance */;\n\n// Get the connectionString field\nFieldInfo connectionStringField = typeof(SqlConnection).GetField("connectionString", BindingFlags.Instance | BindingFlags.NonPublic);\n\n// Append the Application Name parameter\nstring connectionString = (string)connectionStringField.GetValue(connection);\nconnectionString += ";Application Name=YourApplicationName";\n\n// Set the modified connection string\nconnectionStringField.SetValue(connection, connectionString);