string input = "Statements1  start Statements2 ; Statements3 end fun;";\nstring pattern = @"(.*?)\s*(start)\s*(.*?;\s*.*?end.*?)";\n\nMatch match = Regex.Match(input, pattern, RegexOptions.Singleline);\n\nif (match.Success)\n{\n    Console.WriteLine("Match 1: " + match.Groups[1].Value); // Statements1\n    Console.WriteLine("Match 2: " + match.Groups[2].Value); // start\n    Console.WriteLine("Match 3: " + match.Groups[3].Value); // Statements2 ; Statements3 end fun;\n}