protected override void OnSave()\n{\n    base.OnSave();\n\n    // Get the selected category\n    string selectedCategory = ddlCategories.SelectedItem.Text;\n\n    // Save the category to the list\n    SPList list = SPContext.Current.List;\n    list.Fields.Add("MyCustomField", SPFieldType.Text, false);\n    list.Fields["MyCustomField"].Description = selectedCategory;\n    list.Update();\n}