// Model.rs\nusing Microsoft.CodeAnalysis;\nusing Microsoft.CodeAnalysis.CSharp;\n\nclass Model\n{\n    public string Name { get; set; }\n    public Property[] Properties { get; set; }\n}\n\nclass Property\n{\n    public string Name { get; set; }\n    public string Type { get; set; }\n}\n\nvar model = new Model\n{\n    Name = "Person",\n    Properties = new[]\n    {\n        new Property { Name = "Name", Type = "string" },\n        new Property { Name = "Age", Type = "int" }\n    }\n};\n\nvar code = new CSharpSyntaxTree(\n    new[]\n    {\n        new UsingDirective("System"),\n        new NamespaceDeclaration("MyNamespace",\n            new[]\n            {\n                new ClassDeclaration(model.Name,\n                    new[]\n                    {\n                        foreach (var property in model.Properties)\n                        {\n                            new PropertyDeclaration(property.Type, property.Name);\n                        }\n                    })\n            })\n    });\n\nConsole.WriteLine(code.ToString());