using System;\nusing System.DirectoryServices;\n\nclass IISWebsiteLister\n{\n    static void Main(string[] args)\n    {\n        string serverName = "localhost"; // replace with your server name\n        DirectoryEntry iisRoot = new DirectoryEntry("IIS://" + serverName + "/W3SVC");\n\n        foreach (DirectoryEntry site in iisRoot.Children)\n        {\n            if (site.SchemaClassName == "IIsWebServer")\n            {\n                string siteName = site.Properties["ServerComment"].Value.ToString();\n                string bindings = site.Properties["ServerBindings"].Value.ToString();\n\n                Console.WriteLine($"Site Name: {siteName}");\n                Console.WriteLine($"Bindings: {bindings}");\n                Console.WriteLine();\n            }\n        }\n    }\n}