using System.Web.Mvc;\n\npublic static class HtmlHelperExtensions\n{\n    public static MvcHtmlString Button<T>(this HtmlHelper htmlHelper, string text, ActionResult action)\n    {\n        var url = UrlHelper.GenerateUrl(null, action.Action, action.Controller, null, null, null, null);\n        var button = new TagBuilder("button");\n        button.MergeAttribute("type", "submit");\n        button.MergeAttribute("onclick", $"location.href='{url}';return false;");\n        button.InnerHtml = text;\n        return MvcHtmlString.Create(button.ToString());\n    }\n}