using System;\nusing System.Diagnostics;\n\nDateTime start = DateTime.Now;\n\n// do some stuff...\n\nEventLogQuery query = new EventLogQuery("Application", PathType.LogName, $"*[System/TimeCreated[@SystemTime>='{start.ToString("o")}'] and *[System/Provider.Name!='{string.Join("','", ignoredSources)}']]");\nEventLogReader reader = new EventLogReader(query);\n\nforeach (EventRecord rec in reader)\n{\n    Console.WriteLine($"Entry: {rec.FormatDescription()}");\n}