using System;\nusing System.Security.Cryptography;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class X509Example\n{\n    public static void VerifyCertificateChain()\n    {\n        // Load the certificate from a file\n        X509Certificate2 cert = new X509Certificate2("example.cer");\n\n        // Create a new certificate chain\n        X509Chain chain = new X509Chain();\n\n        // Build the certificate chain\n        chain.ChainPolicy.RevocationMode = X509RevocationMode.NoCheck;\n        chain.Build(cert);\n\n        // Verify the certificate chain\n        bool isValid = chain.ChainStatus.Length == 0;\n\n        Console.WriteLine($"Certificate chain is valid: {isValid}");\n    }\n}